#include<iostream>

using namespace std;

int main() {

	int x; 
    
	x = 10; 

	cout << "x = " << x << endl;

	int y; 

	y = 10+20;
    

	cout << "y = " << y << endl;

	string z;

	z = "10+20";

	cout << "z = " << z << endl;

	string s1 = "hemant";
	string s2 = "solanki";

	cout << "s1+s2 = " << s1+s2 << endl;

	cout << "s1 = " << s1 << endl;

	cout << "s2 = " << s2 << endl;

	return 0;
}